import React from "react";

import "./Modal.css";
import { Transition } from "react-transition-group";

const modal = props => {
  const cssClasses = [
    "Modal"
  ];

  return (
    <Transition
    in={props.show}
    timeout={300}
    mountOnEnter
    unmountOnExit
  >
    {state=>{
    <div className={cssClasses.join(' ')}>
    <h1>A Modal</h1>
    <button className="Button" onClick={props.closed}>
      Dismiss
    </button>
  </div>
    }}

    </Transition>
  );
};

export default modal;
